			SqlObjectConverter objectMapper, 
			SqlParameterConverter converter, 
			Cache<Class<?>, SqlObjectDefinition<?>> cache) {
		super();
		this.namingStrategy = namingStrategy;
		this.converter = converter;
		this.objectMapper = objectMapper;
		this.cache = cache;
	}


