		return SqlObjectDefinition.fromClass(objectType, this);
	}


	public int getMaximumLoadDepth() {
		return maximumLoadDepth;
	}

	public Cache<Class<?>, SqlObjectDefinition<?>> getCache() {
		return cache;
	}

	public static SqlObjectConfig DEFAULT = 
