        }

        int remaining = Math.min(byteBuffer.remaining(), 20);
        byte[] chunk = new byte[remaining];

        byteBuffer.get(chunk);
        return chunk;


    }

    @Override
    public void close() throws IOException {
        byteBuffer = null;
